// EditArticle.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditArticle = ({ match, history }) => {
  const [article, setArticle] = useState({
    title: '',
    content: '',
    category: ''
  });

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/articles/${match.params.id}`);
        setArticle(response.data);
      } catch (error) {
        console.error('Error fetching article:', error);
      }
    };

    fetchArticle();
  }, [match.params.id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setArticle({ ...article, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/articles/${match.params.id}`, article);
      console.log(`Article with ID ${match.params.id} updated successfully`);
      history.push('/'); // Redirect back to the article list after update
    } catch (error) {
      console.error('Error updating article:', error);
      console.error('Error response:', error.response);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Edit Article</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input type="text" className="form-control" name="title" value={article.title} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Content</label>
          <textarea className="form-control" name="content" value={article.content} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Category</label>
          <input type="text" className="form-control" name="category" value={article.category} onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-primary">Update</button>
      </form>
    </div>
  );
};

export default EditArticle;
