// ArticleList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Assuming you're using React Router for navigation


const ArticleList = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get('http://localhost:5000/articles');
        setArticles(response.data);
      } catch (error) {
        console.error('Error fetching articles:', error);
      }
    };

    fetchArticles();
  }, []);

  const handleDelete = async (id) => {
    console.log(`Deleting article with ID: ${id}`);
    try {
      await axios.delete(`http://localhost:5000/articles/${id}`);
      console.log(`Article with ID ${id} deleted successfully`);
      setArticles(articles.filter(article => article._id !== id));
    } catch (error) {
      console.error('Error deleting article:', error);
      console.error('Error response:', error.response); // Log the response for further information
    }
  };
  

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Articles</h2>
      <ul className="list-group">
        {articles.map((article) => (
          <li className="list-group-item" key={article._id}>
            <h3>{article.title}</h3>
            <p>{article.content}</p>
            <p>Kategoria: {article.category}</p>
            <button className="btn btn-danger mr-2" onClick={() => handleDelete(article._id)}>Delete</button>
            <Link to={`/edit/${article._id}`}>
              <button className="btn btn-primary">Edit</button>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ArticleList;
