// EditArticle.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const EditArticle = ({ match }) => {
  const [article, setArticle] = useState({
    title: '',
    content: '',
    category: '',
  });

  const history = useHistory();

  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/articles/${match.params.id}`);
        setArticle(response.data);
      } catch (error) {
        console.error('Error fetching article:', error);
      }
    };

    fetchArticle();
  }, [match.params.id]);

  const handleChange = (e) => {
    setArticle({ ...article, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:5000/articles/${match.params.id}`, article);
      console.log(`Article with ID ${match.params.id} updated successfully`);
      history.push('/articles');
    } catch (error) {
      console.error('Error updating article:', error);
      console.error('Error response:', error.response);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Edit Article</h2>
      <div className="form-group">
        <label>Title</label>
        <input
          type="text"
          className="form-control"
          name="title"
          value={article.title}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label>Content</label>
        <textarea
          className="form-control"
          name="content"
          value={article.content}
          onChange={handleChange}
        ></textarea>
      </div>
      <div className="form-group">
        <label>Category</label>
        <input
          type="text"
          className="form-control"
          name="category"
          value={article.category}
          onChange={handleChange}
        />
      </div>
      <button className="btn btn-primary" onClick={handleUpdate}>
        Update
      </button>
    </div>
  );
};

export default EditArticle;
