  //               <div className="row justify-content-center">
  //                 {articles.map((article) => (
  //                   <div className="col-md-4 mb-4" key={article._id}>
  //                     <div className="card">
  //                       <div className="card-body">
  //                         <h5 className="card-title">{article.title}</h5>
  //                         <p className="card-text">{article.content}</p>
  //                         <p className="card-text"><small className="text-muted">Kategoria: {article.category}</small></p>
  //                         <button
  //                           className="btn btn-danger"
  //                           onClick={() => handleDelete(article._id)}
  //                         >
  //                           Delete
  //                         </button>
  //                         <button
  //                             className="btn btn-primary mr-2"
  //                             onClick={() => handleEdit(article)}
  //                           >
  //                             Edit
  //                         </button>
  //                       </div>
  //                     </div>
  //                   </div>
  //                 ))}
  //               </div>
  //               )}
  //             </div>




  // );

  <div className="row justify-content-center">
  {articles.map((article) => {
    // Find the category with the matching ID
    const selectedCategory = categories.find(cat => cat._id === article.category);

    return (
      <div className="col-md-4 mb-4" key={article._id}>
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">{article.title}</h5>
            <p className="card-text">{article.content}</p>
            {/* Display category name */}
            <p className="card-text">
              <small className="text-muted">Kategoria: {selectedCategory ? selectedCategory.name : ''}</small>
            </p>
            <button
              className="btn btn-danger"
              onClick={() => handleDelete(article._id)}
            >
              Delete
            </button>
            <button
              className="btn btn-primary mr-2"
              onClick={() => handleEdit(article)}
            >
              Edit
            </button>
          </div>
        </div>
      </div>
    );
  })}
</div>

};

export default ArticleList;
