// App.js
import React from 'react';
import { BrowserRouter as Routeri, Routes, Route, Link } from 'react-router-dom';
import ArticleForm from './components/ArticleForm';
import CategoryForm from './components/CategoryForm';
import ArticleList from './components/ArticleList';
import CategoryList from './components/CategoryList';

const App = () => {
  return (
    <Routeri>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/articles">Articles</Link>
            </li>
            <li>
              <Link to="/categories">Categories</Link>
            </li>
            <li>
              <Link to="/articles/add">Shto nje artikull</Link>
            </li>
            <li>
              <Link to="/categories/add">Shto nje kategori</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route path="/articles" element={<ArticleList/>} />
          <Route path="/articles/add" element={<ArticleForm/>} />
          <Route path="/categories"  element={<CategoryList/>} />
          <Route path="/categories/add" element={<CategoryForm/>} />
          <Route path="/edit-article/:id" element={<EditArticleForm/>} />
        </Routes>

      </div>
    </Routeri>
  );
};

export default App;
